mapper.select:
SELECT ARTICLE_NO, USER_ID, SUBJECT, CONTENT, HIT, REGISTER_TIME
FROM BOARD





					SUBJECT LIKE CONCAT('%', #{word}, '%')
					OR USER_ID = #{word}


					SUBJECT LIKE CONCAT('%', #{word}, '%')


					USER_ID = #{word}





ORDER BY ARTICLE_NO DESC LIMIT #{START}, #{SPL}

SELECT count(ARTICLE_NO) FROM BOARD
WHERE SUBJECT LIKE CONCAT('%', #{word}, '%') OR USER_ID = #{word}
WHERE SUBJECT LIKE CONCAT('%', #{word}, '%')
WHERE USER_ID = #{word}

select article_no, user_id, subject, content, hit, register_time
from board
where article_no = #{articleNo}

mapper.update:
update board
set hit = hit + 1
where article_no = #{articleNo}

update board
set subject = #{subject}, content = #{content}
where article_no = #{articleNo}
